int main(int argc, char **argv) {

	// If inflation worked, give strip set, strip map, and related data and make color map full-image. 
	Error e = cmGen(&biggerCM);

	/************* SDL2 example starts here! ***************/
	if (SDL_Init(SDL_INIT_VIDEO) != SUCCESS)
		return EXIT_FAILURE;

	SDL_Window *windowP = SDL_CreateWindow("Hello world!", 100, 100, 1080, 700, SDL_WINDOW_SHOWN);
	if (!windowP)
		return EXIT_FAILURE;
	SDL_Surface *wSurfP = SDL_GetWindowSurface(windowP);
	SDL_Rect wRect = {0, 0, 620, 387};

	SDL_Renderer *rendererP = SDL_CreateRenderer(windowP, -1, SDL_RENDERER_ACCELERATED | SDL_RENDERER_PRESENTVSYNC);
	if (!rendererP) {
		SDL_DestroyWindow(windowP);
		SDL_Quit();
		return EXIT_FAILURE;
	}

	SDL_Surface *surfaceP = SDL_CreateRGBSurfaceWithFormat(0, biggerCM.w, biggerCM.h, biggerCM.bpp, SDL_PIXELFORMAT_INDEX8);

	if (!surfaceP) {
		e = E_BAD_ARGS;
		printf("failed to make w: %d, h: %d, bpp: %d, pitch:%d surface: %s\n", biggerCM.w, biggerCM.h, biggerCM.bpp, biggerCM.pitch, SDL_GetError());
	}
	else {
		// Decouple the SDL renderer specifics from Jollybean.

		SDL_Palette palette1 = {16, colorSet1, 0, 0};
		surfaceP->pixels = biggerCM.dataP;
		e = SDL_SetSurfacePalette(surfaceP, &palette1);
	}

	for (int i = 0; i < biggerCM.w * biggerCM.h; i++) {
		//printf("%d ", biggerCM.dataP[i]);
	}

	SDL_Texture *tP = SDL_CreateTextureFromSurface(rendererP, surfaceP);
	printf("copy error: %s\n", SDL_GetError());
	if (!tP) {
		printf("texture error: %s\n", SDL_GetError());
		e = E_BAD_ARGS;
	}
	if (!e) {
		e = SDL_SetTextureBlendMode(tP, SDL_BLENDMODE_BLEND);
	}

	SDL_Rect srcRect = {0, 0, 128, 256};
	if (!e) {
		SDL_Rect dstRect = {100, 100, 64, 256};
		for (int i = 0; i < 500; i++) {
			SDL_SetRenderDrawColor(rendererP, 0, 0, 0x33, 0xff);
			SDL_RenderClear(rendererP);
			e = SDL_RenderCopy(rendererP, tP, &srcRect, &dstRect);
			dstRect.x++;
			SDL_RenderPresent(rendererP);
		}
	}
	sleep(5);

		
	SDL_DestroyRenderer(rendererP);
	SDL_DestroyWindow(windowP);
	SDL_Quit();

	/*******************************************************/

	cmClr(&biggerCM);

	return e;
}


